def get_penalty_idx(penalty_obj):
    # 为了后续比较相对性，保证标签从小到大刑罚程度增加
    if penalty_obj["death_penalty"]:
        return 14
    elif penalty_obj["life_imprisonment"]:
        return 13
    elif (time:=penalty_obj["imprisonment"]) > 0:
        if time > 120:
            # 十年以上
            return 12
        elif time > 84:
            # 七年以上，十年以下
            return 11
        elif time > 60:
            # 五年以上，七年以下
            return 10
        elif time > 36:
            # 三年以上，五年以下
            return 9
        elif time > 24:
            # 两年以上，三年以下
            return 8
        elif time > 12:
            # 一年以上，两年以下
            return 7
        elif time > 9:
            # 九月以上，一年以下
            return 6
        elif time > 6:
            # 六月以上，九月以下
            return 5
        else:
            return 4
    elif penalty_obj["detention"] > 0:
        # 拘役
        return 3
    elif (time := penalty_obj["surveillance"]) > 0:
        if time > 12:
            # 管制一年以上
            return 2
        else:
            # 管制一年以下
            return 1
    else:
        # 免予刑事处罚
        return 0


def get_charge_article_penalty_labels(judgments, article_predict_level="article"):
    charge_label = [0] * TOTAL_CHARGE_NUM
    article_label = [0] * TOTAL_ARTICLE_NUM if article_predict_level == "article" else [0] * TOTAL_ARTICLE_PARAGRAPH_NUM
    penalty_label = [0] * TOTAL_PENALTY_NUM
    for ac in judgments:
        charge_idx = get_charge_idx(ac["standard_accusation"])
        if charge_idx != -1:
            charge_label[charge_idx] = 1
        for art in ac["article"]:
            art_idx = get_article_idx(art, predict_level=article_predict_level)
            if art_idx != -1:
                article_label[art_idx] = 1
    penalty = judgments["final_penalty"] if "final_penalty" in judgments else judgments[0]["penalty"]
    penalty_idx = get_penalty_idx(penalty)
    penalty_label[penalty_idx] = 1
    return charge_label, article_label, penalty_label

